class MemoryLeakExample {
    private static final List<Object> CACHE = new ArrayList<>();

    public void addToCache(Object obj) {
        CACHE.add(obj);  // Objects never removed
    }

    // Better approach using WeakReference
    private static final List<WeakReference<Object>> WEAK_CACHE = new ArrayList<>();

    public void addToWeakCache(Object obj) {
        WEAK_CACHE.add(new WeakReference<>(obj));
    }
}

