import java.io.*;
import java.net.*;

// GET request
URL url = new URL("https://api.example.com/data");
HttpURLConnection conn = (HttpURLConnection) url.openConnection();
conn.setRequestMethod("GET");

int responseCode = conn.getResponseCode();
if (responseCode == HttpURLConnection.HTTP_OK) {
    BufferedReader in = new BufferedReader(
        new InputStreamReader(conn.getInputStream()));
    String inputLine;
    StringBuilder response = new StringBuilder();

    while ((inputLine = in.readLine()) != null) {
        response.append(inputLine);
    }
    in.close();

    System.out.println(response.toString());
}

// POST request with JSON
URL postUrl = new URL("https://api.example.com/users");
HttpURLConnection postConn = (HttpURLConnection) postUrl.openConnection();
postConn.setRequestMethod("POST");
postConn.setRequestProperty("Content-Type", "application/json");
postConn.setRequestProperty("Authorization", "Bearer token123");
postConn.setDoOutput(true);

String jsonInputString = "{\"name\":\"John\",\"age\":30}";

try(OutputStream os = postConn.getOutputStream()) {
    byte[] input = jsonInputString.getBytes("utf-8");
    os.write(input, 0, input.length);
}

int postResponseCode = postConn.getResponseCode();
if (postResponseCode == HttpURLConnection.HTTP_CREATED) {
    try(BufferedReader br = new BufferedReader(
        new InputStreamReader(postConn.getInputStream(), "utf-8"))) {
        StringBuilder response = new StringBuilder();
        String responseLine = null;
        while ((responseLine = br.readLine()) != null) {
            response.append(responseLine.trim());
        }
        System.out.println(response.toString());
    }
}

import java.net.URI;
import java.net.http.*;
import java.net.http.HttpResponse.BodyHandlers;

// GET request
HttpClient client = HttpClient.newHttpClient();
HttpRequest request = HttpRequest.newBuilder()
    .uri(URI.create("https://api.example.com/data"))
    .build();

HttpResponse<String> response = client.send(request, BodyHandlers.ofString());
System.out.println(response.body());

// POST request with JSON
String json = "{\"name\":\"John\",\"age\":30}";

HttpRequest postRequest = HttpRequest.newBuilder()
    .uri(URI.create("https://api.example.com/users"))
    .header("Content-Type", "application/json")
    .header("Authorization", "Bearer token123")
    .POST(HttpRequest.BodyPublishers.ofString(json))
    .build();

HttpResponse<String> postResponse = client.send(postRequest, BodyHandlers.ofString());
System.out.println(postResponse.body());

// Async request
client.sendAsync(request, BodyHandlers.ofString())
    .thenApply(HttpResponse::body)
    .thenAccept(System.out::println);

import org.springframework.web.reactive.function.client.WebClient;
import reactor.core.publisher.Mono;

// Create WebClient
WebClient client = WebClient.builder()
    .baseUrl("https://api.example.com")
    .defaultHeader("Authorization", "Bearer token123")
    .build();

// GET request
Mono<String> response = client.get()
    .uri("/data")
    .retrieve()
    .bodyToMono(String.class);

response.subscribe(System.out::println);

// POST request with JSON
Mono<String> postResponse = client.post()
    .uri("/users")
    .contentType(MediaType.APPLICATION_JSON)
    .bodyValue("{\"name\":\"John\",\"age\":30}")
    .retrieve()
    .bodyToMono(String.class);

postResponse.subscribe(System.out::println);

// Error handling
client.get()
    .uri("/data")
    .retrieve()
    .onStatus(HttpStatus::is4xxClientError, response -> {
        return Mono.error(new RuntimeException("Client error"));
    })
    .onStatus(HttpStatus::is5xxServerError, response -> {
        return Mono.error(new RuntimeException("Server error"));
    })
    .bodyToMono(String.class);

import org.springframework.web.bind.annotation.*;
import org.springframework.http.*;

@RestController
@RequestMapping("/api")
public class UserController {

    @GetMapping("/users")
    public ResponseEntity<List<User>> getUsers() {
        List<User> users = userService.getAllUsers();
        return ResponseEntity.ok(users);
    }

    @GetMapping("/users/{id}")
    public ResponseEntity<User> getUser(@PathVariable Long id) {
        User user = userService.getUserById(id);
        if (user == null) {
            return ResponseEntity.notFound().build();
        }
        return ResponseEntity.ok(user);
    }

    @PostMapping("/users")
    public ResponseEntity<User> createUser(@RequestBody User user) {
        User createdUser = userService.createUser(user);
        return ResponseEntity
            .created(URI.create("/api/users/" + createdUser.getId()))
            .body(createdUser);
    }

    @PutMapping("/users/{id}")
    public ResponseEntity<User> updateUser(
            @PathVariable Long id, 
            @RequestBody User user) {
        User updatedUser = userService.updateUser(id, user);
        if (updatedUser == null) {
            return ResponseEntity.notFound().build();
        }
        return ResponseEntity.ok(updatedUser);
    }

    @DeleteMapping("/users/{id}")
    public ResponseEntity<Void> deleteUser(@PathVariable Long id) {
        if (userService.deleteUser(id)) {
            return ResponseEntity.noContent().build();
        }
        return ResponseEntity.notFound().build();
    }
}

