LocalDate parsed = LocalDate.parse("2023-01-01");
System.out.println("Parsed date: " + parsed);

// Time arithmetic
LocalDateTime tomorrow = now.plusDays(1);
System.out.println("Tomorrow: " + tomorrow);

// Time comparison
boolean isAfter = tomorrow.isAfter(now);
System.out.println("Is tomorrow after now? " + isAfter);

// Timezone
ZoneId nyZone = ZoneId.of("America/New_York");
ZonedDateTime nyTime = now.atZone(nyZone);
System.out.println("New York time: " + nyTime);

// Unix timestamp
long timestamp = now.toEpochSecond(ZoneOffset.UTC);
System.out.println("Unix timestamp: " + timestamp);

// Time components
System.out.printf("Components: %d-%02d-%02d %02d:%02d:%02d%n",
    now.getYear(), now.getMonthValue(), now.getDayOfMonth(),
    now.getHour(), now.getMinute(), now.getSecond());

// Duration
Duration duration = Duration.between(now, tomorrow);
System.out.println("Duration: " + duration.toHours() + " hours");

// Period
Period period = Period.between(parsed, LocalDate.now());
System.out.println("Period: " + period.getYears() + " years");

import java.sql.*;

// Connection setup
String url = "jdbc:mysql://localhost:3306/mydb";
String username = "user";
String password = "pass";

try (Connection conn = DriverManager.getConnection(url, username, password)) {
    // Create statement
    Statement stmt = conn.createStatement();

    // Execute query
    ResultSet rs = stmt.executeQuery("SELECT * FROM users");

    // Process results
    while (rs.next()) {
        int id = rs.getInt("id");
        String name = rs.getString("name");
        System.out.println(id + ": " + name);
    }

    // Prepared statement
    PreparedStatement pstmt = conn.prepareStatement(
        "INSERT INTO users (name, email) VALUES (?, ?)");
    pstmt.setString(1, "John");
    pstmt.setString(2, "john@example.com");
    pstmt.executeUpdate();
}

try (Connection conn = DriverManager.getConnection(url, username, password)) {
    // Disable auto-commit
    conn.setAutoCommit(false);

    try {
        // Execute multiple operations
        PreparedStatement pstmt1 = conn.prepareStatement(
            "UPDATE accounts SET balance = balance - ? WHERE id = ?");
        pstmt1.setDouble(1, 100.0);
        pstmt1.setInt(2, 1);
        pstmt1.executeUpdate();

        PreparedStatement pstmt2 = conn.prepareStatement(
            "UPDATE accounts SET balance = balance + ? WHERE id = ?");
        pstmt2.setDouble(1, 100.0);
        pstmt2.setInt(2, 2);
        pstmt2.executeUpdate();

        // Commit transaction
        conn.commit();
    } catch (SQLException e) {
        // Rollback on error
        conn.rollback();
        throw e;
    }
}

import org.hibernate.*;
import javax.persistence.*;

// Entity class
@Entity
@Table(name = "users")
class User {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @Column(name = "name")
    private String name;

    @Column(name = "email")
    private String email;

    // Getters and setters
}

// Using Hibernate
SessionFactory factory = new Configuration()
    .configure()
    .addAnnotatedClass(User.class)
    .buildSessionFactory();

try (Session session = factory.openSession()) {
    // Begin transaction
    session.beginTransaction();

    // Create user
    User user = new User();
    user.setName("John");
    user.setEmail("john@example.com");
    session.save(user);

    // Query users
    List<User> users = session.createQuery("FROM User", User.class)
        .getResultList();

    // Commit transaction
    session.getTransaction().commit();
}

