        frame.setSize(400, 300);
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.setVisible(true);
    }
}

import javafx.application.Application;
import javafx.scene.Scene;
import javafx.scene.control.Button;
import javafx.scene.layout.StackPane;
import javafx.stage.Stage;

public class SceneSwitcher extends Application {
    @Override
    public void start(Stage stage) {
        Button toSecond = new Button("Go to Second Scene");
        StackPane firstLayout = new StackPane(toSecond);
        Scene firstScene = new Scene(firstLayout, 300, 200);

        Button toFirst = new Button("Back to First Scene");
        StackPane secondLayout = new StackPane(toFirst);
        Scene secondScene = new Scene(secondLayout, 300, 200);

        toSecond.setOnAction(e -> stage.setScene(secondScene));
        toFirst.setOnAction(e -> stage.setScene(firstScene));

        stage.setTitle("Scene Switcher");
        stage.setScene(firstScene);
        stage.show();
    }

    public static void main(String[] args) {
        launch();
    }
}

